//为了避免我们定义的变量和其他插件变量之间发生冲突
//我们采用匿名函数自调用形式
//使用()把匿名函数括起来，后面加上() 来达到调用的目的，调用时传递了jQuery构造器
(function (){
	//方法1：基于构造器的扩展 
	//扩展两个功能，计算两个变量和，计算两个变量差
	jQuery.extend({
		add:function(a,b){
			var n = a + b;
			return n;
		},
		sub:function(a,b){
			var res = a - b;
			return res;
		}
	});
	//在jQuery实例(对象)身上扩展功能
	//jQuery允许传递一些额外的参数，但是jQuery也会允许你设置默认的参数
	jQuery.fn.changeColor = function(opts){
		//这是默认的参数
		var defaults = {
			foreround:'#666',
			background:'#fff'
		};
		//可以将这些参数合并，使用传递的覆盖默认的参数
		jQuery.fn.extend(defaults,opts);
		//代表当前对象，在哪里获得
		$(this).css('color',defaults.foreround);
		$(this).css('background',defaults.background);
	}

	//图片轮播图插件
	jQuery.fn.changePic = function(time){
		change = function(){
			var oimg = $('img').attr('src');
			var now = oimg.substr(4,1);
			now++;
			if(now>8){
				now = 1;
			}
			$('img').attr('src','img/'+now+'.jpg');
		}
		setInterval(change, time);
	}
})(jQuery);